import{_ as v}from"./plugin-vueexport-helper-BMQQWPAP.js";import{o,c as m,U as s,O as e,a as h,T as n,u as d,S as i,F as p,a7 as g,M as u,av as A,aw as T,ah as l,as as M}from"./@vue-1w769d10.js";import{E as b,a as y,b as z,c as P,i as H}from"./element-plus-BMsp6j4-.js";/* empty css                     */import"./lodash-es-WEemOz_S.js";import"./@vueuse-CwK-ZWOg.js";import"./@element-plus-BvmR4Vcl.js";import"./@popperjs-D9SI2xQl.js";import"./@ctrl-r5W6hzzQ.js";import"./dayjs-Ct2Knyoi.js";import"./async-validator-DKvM95Vc.js";import"./memoize-one-BdPwpGay.js";import"./normalize-wheel-es-B6fDCfyv.js";import"./@floating-ui-CtcGg-6p.js";const _=t=>(A("data-v-b4eaf9b4"),t=t(),T(),t),U={class:"disease-detail"},D=_(()=>h("br",null,null,-1)),G=_(()=>h("br",null,null,-1)),N={__name:"DiseaseDetail",props:{diseaseData:{type:Object,required:!0}},setup(t){return(f,w)=>(o(),m("div",U,[s(d(P),{class:"box-card",shadow:"hover"},{default:e(()=>[h("h2",null,n(t.diseaseData.disease.zh)+"（"+n(t.diseaseData.disease.en)+"）",1),h("p",null,n(t.diseaseData.description.zh),1),h("p",null,n(t.diseaseData.description.en),1),s(d(b),null,{default:e(()=>[i("推荐草药")]),_:1}),s(d(y),{bordered:"",column:"1"},{default:e(()=>[(o(!0),m(p,null,g(t.diseaseData.recommended_herbs,(a,r)=>(o(),u(d(z),{key:r},{label:e(()=>[i(n(a.name.zh)+" ("+n(a.name.en)+")",1)]),default:e(()=>[i(" "+n(a.effects.zh),1),D,i(n(a.effects.en),1)]),_:2},1024))),128))]),_:1}),s(d(b),null,{default:e(()=>[i("草药搭配")]),_:1}),s(d(y),{bordered:"",column:"1"},{default:e(()=>[(o(!0),m(p,null,g(t.diseaseData.herbal_combinations,(a,r)=>(o(),u(d(z),{key:`combo-${r}`},{label:e(()=>[i(n(a.combination.zh)+" ("+n(a.combination.en)+")",1)]),default:e(()=>[i(" "+n(a.effects.zh),1),G,i(n(a.effects.en),1)]),_:2},1024))),128))]),_:1})]),_:1})]))}},L=v(N,[["__scopeId","data-v-b4eaf9b4"]]),B={zh:"心绞痛",en:"Angina Pectoris"},E="angina",V={zh:"心绞痛是一种胸部疼痛的症状，通常由心脏供血不足导致。这种疼痛感可能会辐射到臂部、肩部、颈部或下颚。心绞痛是冠状动脉疾病的一种表现，提示心脏某部分暂时得不到足够的血液和氧气。在中医中，心绞痛可能被视为“心痛”或“胸痹”，其治疗侧重于活血化瘀、通经络、散寒解郁。",en:'Angina Pectoris is a symptom of chest pain, often due to insufficient blood supply to the heart, which may radiate to the arms, shoulders, neck, or jaw. It is an indication of coronary artery disease, signaling that a part of the heart is not receiving enough blood and oxygen temporarily. In traditional Chinese medicine (TCM), angina is often treated as "heart pain" or "chest impediment," focusing on promoting blood circulation, removing blood stasis, dredging collaterals, dispelling cold, and resolving depression.'},R=[{name:{zh:"茯苓",en:"Poria"},effects:{zh:"温和的利水剂，能够健脾、利湿、宁心安神",en:"A mild diuretic that can strengthen the spleen, drain dampness, calm the heart, and soothe the mind"}},{name:{zh:"当归",en:"Angelica Sinensis"},effects:{zh:"补血调经、活血化瘀，改善血液循环",en:"Blood-supplementing and stasis-resolving, improves blood circulation"}},{name:{zh:"丹参",en:"Salvia Miltiorrhiza"},effects:{zh:"活血化瘀、舒张血管，对冠状动脉疾病和心绞痛有良好的治疗效果",en:"Promotes blood circulation and vessel dilation, effective for coronary artery disease and angina"}},{name:{zh:"山楂",en:"Hawthorn"},effects:{zh:"促进血液循环，强化心脏功能，降低血压和胆固醇",en:"Promotes blood circulation, strengthens cardiac function, and lowers blood pressure and cholesterol"}}],j=[{combination:{zh:"茯苓和丹参",en:"Poria and Salvia Miltiorrhiza"},effects:{zh:"共同改善心脏的血液供应，减轻心绞痛症状",en:"Improves cardiac blood supply and relieves symptoms of angina"}},{combination:{zh:"当归和山楂",en:"Angelica Sinensis and Hawthorn"},effects:{zh:"补血活血，强化心脏功能，适合心绞痛患者，尤其是那些因血液循环不畅导致的症状",en:"Supplements and activates blood circulation, strengthens cardiac function, suitable for angina patients, especially those with symptoms caused by poor blood circulation"}}],F={disease:B,key:E,description:V,recommended_herbs:R,herbal_combinations:j},O={zh:"心律失常",en:"Arrhythmia"},W="arrhythmia",J={zh:"心律失常是指心脏的节律或速率异常。它可以表现为心跳过快、过慢或不规则。心律失常有多种类型，包括心动过速、心动过缓、房颤等，可能由心脏疾病、电解质不平衡、心脏手术后效应或某些药物引起。在中医中，心律失常可能与‘心气乱’或‘心阴虚’有关，治疗侧重于调和心脏功能、滋养心阴、安神。",en:"Arrhythmia is an abnormal rhythm or rate of the heartbeat. It can manifest as the heart beating too fast, too slow, or irregularly. There are various types of arrhythmias, including tachycardia, bradycardia, atrial fibrillation, etc., which can be caused by heart disease, electrolyte imbalances, effects of heart surgery, or certain medications. In traditional Chinese medicine (TCM), arrhythmia may be related to 'heart qi disorder' or 'heart yin deficiency,' with treatments focusing on harmonizing heart function, nourishing heart yin, and calming the mind."},K=[{name:{zh:"白术",en:"Atractylodes Macrocephala"},effects:{zh:"能健脾燥湿、补气固表，对于因脾虚引起的心律失常有一定的辅助治疗作用，通过增强脾功能来稳定内环境，进而稳定心律。",en:"Strengthens the spleen, dries dampness, supplements qi, and secures the exterior. It offers auxiliary treatment for arrhythmias caused by spleen deficiency, by enhancing spleen function to stabilize the internal environment and thus stabilize the heartbeat."}},{name:{zh:"丹参",en:"Salvia Miltiorrhiza"},effects:{zh:"具有活血化瘀、清热解毒的功效，能够改善心脏的血液循环，对于心律失常有一定的缓解作用。",en:"Has the effects of promoting blood circulation to remove blood stasis and clearing heat and detoxifying. It can improve cardiac blood circulation and relieve arrhythmias."}},{name:{zh:"桔梗",en:"Platycodon Grandiflorum"},effects:{zh:"能够宣肺解表、利咽通络，对于因肺部疾病引起的心律失常有辅助治疗作用，帮助改善呼吸系统的健康状况。",en:"Can disperse the lung and resolve phlegm, benefit the throat, and promote the circulation of qi. It offers auxiliary treatment for arrhythmias caused by lung diseases, helping to improve the health of the respiratory system."}},{name:{zh:"香附",en:"Cyperus Rotundus"},effects:{zh:"具有疏肝解郁、行气活血的作用，可以缓解因情绪波动或气滞血瘀引起的心律失常，有助于维持心脏的正常节律。",en:"Has the functions of soothing the liver, relieving depression, promoting qi circulation, and activating blood. It can alleviate arrhythmias caused by emotional fluctuations or qi stagnation and blood stasis, helping to maintain a normal heart rhythm."}}],Q=[{combination:{zh:"丹参和白术",en:"Salvia Miltiorrhiza and Atractylodes Macrocephala"},effects:{zh:"可以活血化瘀、补气固表，对于改善心脏血液循环、稳定心律具有积极效果。",en:"Can promote blood circulation to remove blood stasis and supplement qi, having a positive effect on improving cardiac blood circulation and stabilizing the heartbeat."}},{combination:{zh:"香附与桔梗",en:"Cyperus Rotundus and Platycodon Grandiflorum"},effects:{zh:"可以疏肝解郁、宣肺解表，适合因情绪波动或呼吸系统疾病引起的心律失常。",en:"Can soothe the liver, relieve depression, disperse the lung, and resolve phlegm, suitable for arrhythmias caused by emotional fluctuations or respiratory diseases."}}],X={disease:O,key:W,description:J,recommended_herbs:K,herbal_combinations:Q},Y={zh:"咳嗽",en:"Cough"},Z="cough",ee={zh:"咳嗽是一种常见的呼吸系统疾病，主要症状包括反复的咳嗽声，有时伴有痰。咳嗽可以由多种原因引起，包括感冒、流感、呼吸道感染、过敏、吸烟等。在中医中，治疗咳嗽往往需要辨证施治，根据咳嗽的具体类型（如风寒咳嗽、风热咳嗽等）来选择合适的草药。",en:"Cough is a common respiratory disease characterized by repetitive coughing sounds, sometimes accompanied by phlegm. It can be caused by various factors, including colds, influenza, respiratory infections, allergies, smoking, etc. In traditional Chinese medicine (TCM), treatment for cough often requires diagnosis and treatment based on the specific type of cough (such as wind-cold cough, wind-heat cough, etc.), and appropriate herbs are selected accordingly."},ne=[{name:{zh:"甘草",en:"Licorice"},effects:{zh:"清热解毒、润肺止咳",en:"Clear heat and detoxify, moisten the lungs, and stop coughs"}},{name:{zh:"当归",en:"Angelica Sinensis"},effects:{zh:"补血调经、活血化瘀",en:"Blood supplementation and menstruation regulation, improve blood circulation"}},{name:{zh:"丹参",en:"Salvia Miltiorrhiza"},effects:{zh:"活血化瘀、舒张血管",en:"Improve blood circulation and vessel dilation"}},{name:{zh:"菊花",en:"Chrysanthemum"},effects:{zh:"清热解毒、平肝明目",en:"Clear heat, detoxify, calm the liver, and improve vision"}}],te=[{combination:{zh:"甘草和菊花",en:"Licorice and Chrysanthemum"},effects:{zh:"清热解毒、润肺止咳",en:"Heat-clearing, detoxifying, and cough-relieving effects"}},{combination:{zh:"当归和丹参",en:"Angelica Sinensis and Salvia Miltiorrhiza"},effects:{zh:"补血活血，适用于因气血虚弱引起的慢性咳嗽",en:"Blood-supplementing and circulation-improving effects, suitable for chronic coughs due to qi and blood deficiency"}},{combination:{zh:"丹参单独或与其他药材搭配",en:"Salvia Miltiorrhiza alone or in combination with other herbs"},effects:{zh:"改善血液循环，减轻咳嗽",en:"Improve blood circulation and alleviate coughing"}}],ae={disease:Y,key:Z,description:ee,recommended_herbs:ne,herbal_combinations:te},ie={zh:"膀胱炎",en:"Cystitis"},se="cystitis",oe={zh:"膀胱炎是指膀胱的炎症，通常表现为频繁、急迫感以及疼痛的排尿。膀胱炎可能由多种原因引起，包括感染、药物、辐射治疗或某些疾病。在中医中，膀胱炎可能与湿热下注、气滞血瘀有关，治疗通常侧重于清热利湿、活血化瘀。",en:"Cystitis refers to the inflammation of the bladder, usually presenting as frequent, urgent, and painful urination. The condition can be caused by various factors, including infections, medications, radiation therapy, or certain diseases. In traditional Chinese medicine (TCM), cystitis may be associated with damp-heat pouring down or qi stagnation and blood stasis, with treatments typically focusing on clearing heat and dampness, promoting blood circulation to remove blood stasis."},re=[{name:{zh:"茯苓",en:"Poria"},effects:{zh:"是一种温和的利水剂，能健脾利湿，对于因湿热引起的膀胱炎有很好的治疗效果",en:"A mild diuretic that can strengthen the spleen and drain dampness, effective for treating cystitis caused by damp-heat"}},{name:{zh:"当归",en:"Angelica Sinensis"},effects:{zh:"能补血调经、活血化瘀，对于因气血不畅引起的膀胱炎症状，如疼痛、血尿，有一定的缓解作用",en:"Can supplement blood, regulate menstruation, promote blood circulation, and remove blood stasis, relieving symptoms of cystitis such as pain and hematuria"}},{name:{zh:"泽泻",en:"Alisma Plantago-aquatica"},effects:{zh:"具有利水渗湿、清热泻火的功效，适用于治疗因内热过盛引起的膀胱炎",en:"Has diuretic, dampness-draining, heat-clearing, and fire-purging effects, suitable for treating cystitis caused by excessive internal heat"}},{name:{zh:"桔梗",en:"Platycodon Grandiflorum"},effects:{zh:"能够宣肺解表、利咽通络，对于上呼吸道感染引起的膀胱炎有辅助治疗作用",en:"Can disperse the lung and resolve phlegm, benefiting the throat and promoting the circulation of qi, provides auxiliary treatment for cystitis caused by upper respiratory tract infections"}}],ce=[{combination:{zh:"茯苓和泽泻",en:"Poria and Alisma Plantago-aquatica"},effects:{zh:"可以加强利水渗湿的作用，特别适合湿热型膀胱炎患者",en:"Strengthens the diuresis and dampness-draining effect, especially suitable for patients with damp-heat type cystitis"}},{combination:{zh:"当归与桔梗",en:"Angelica Sinensis and Platycodon Grandiflorum"},effects:{zh:"不仅可以活血化瘀，还可以帮助清热解毒，适合因气血不畅或感染引起的膀胱炎",en:"Not only promotes blood circulation to remove blood stasis but also helps to clear heat and detoxify, suitable for cystitis caused by qi and blood stagnation or infections"}}],de={disease:ie,key:se,description:oe,recommended_herbs:re,herbal_combinations:ce},le={zh:"胃肠功能紊乱",en:"Gastrointestinal Dysregulation"},he="gastrointestinal",me={zh:"胃肠功能紊乱指的是胃肠道功能失常，可能包括消化不良、腹胀、胃痛、腹泻或便秘等症状。这种情况可能由饮食习惯不当、情绪压力、胃肠炎症或慢性疾病等多种因素引起。在中医中，胃肠功能紊乱可能与脾胃虚弱、湿热内蕴、气滞血瘀等有关，治疗通常侧重于健脾和胃、调理气血、清热利湿。",en:"Gastrointestinal Dysregulation refers to a dysfunction of the gastrointestinal (GI) tract, which may include symptoms such as indigestion, bloating, stomach pain, diarrhea, or constipation. This condition can be caused by improper dietary habits, emotional stress, inflammation of the GI tract, or chronic diseases. In traditional Chinese medicine (TCM), gastrointestinal dysregulation may be related to weakness of the spleen and stomach, damp-heat accumulation, qi stagnation and blood stasis, with treatments focusing on strengthening the spleen and stomach, regulating qi and blood, and clearing heat and draining dampness."},ue=[{name:{zh:"甘草",en:"Licorice"},effects:{zh:"具有补脾益气、清热解毒的作用，能够缓解胃部不适和胃炎症状。甘草也可用于调和其他草药，增强治疗效果。",en:"Supplements the spleen and benefits qi, clears heat and detoxifies, relieving stomach discomfort and symptoms of gastritis. Licorice can also harmonize other herbs, enhancing the therapeutic effect."}},{name:{zh:"当归",en:"Angelica Sinensis"},effects:{zh:"能补血调经、活血化瘀，对于因气血不畅引起的胃肠功能紊乱，如腹痛、消化不良，有一定的缓解作用。",en:"Can supplement blood, regulate menstruation, promote blood circulation, and remove blood stasis. It can relieve gastrointestinal dysregulation caused by qi and blood disharmony, such as abdominal pain and indigestion."}},{name:{zh:"白术",en:"Atractylodes Macrocephala"},effects:{zh:"能健脾燥湿、补气固表，对于脾虚引起的胃肠功能紊乱（如食欲不振、腹胀）特别有效。它能够促进食物的消化吸收，提高身体的整体能量水平。",en:"Strengthens the spleen, dries dampness, supplements qi, and secures the exterior. It is particularly effective for gastrointestinal dysregulation caused by spleen deficiency (such as poor appetite and bloating). It promotes the digestion and absorption of food, improving the overall energy level of the body."}},{name:{zh:"山楂",en:"Hawthorn"},effects:{zh:"能够促进消化、去食积，对于因饮食过量或食物积滞引起的胃肠功能紊乱有显著效果。山楂还可以帮助分解脂肪，适合消化油腻食物不良。",en:"Promotes digestion and removes food stagnation, significantly effective for gastrointestinal dysregulation caused by overeating or food stagnation. Hawthorn can also help break down fats, suitable for poor digestion of greasy foods."}}],fe=[{combination:{zh:"甘草和白术",en:"Licorice and Atractylodes Macrocephala"},effects:{zh:"可以共同作用于健脾补气，增强消化功能，适合大部分胃肠功能紊乱的患者。",en:"Work together to strengthen the spleen and supplement qi, enhancing digestive function, suitable for most patients with gastrointestinal dysregulation."}},{combination:{zh:"当归与山楂",en:"Angelica Sinensis and Hawthorn"},effects:{zh:"不仅可以补血活血，还能促进消化，适用于因气血不畅或食积导致的胃肠功能紊乱。",en:"Not only supplements blood and promotes blood circulation but also promotes digestion, suitable for gastrointestinal dysregulation caused by qi and blood disharmony or food stagnation."}}],pe={disease:le,key:he,description:me,recommended_herbs:ue,herbal_combinations:fe},ge={zh:"消化不良",en:"Indigestion"},be="indigestion",ye={zh:"消化不良是指消化过程中出现的问题，导致胃部不适、腹胀、腹痛、恶心或反酸等症状。消化不良可以由多种因素引起，包括饮食不当、过度饮酒、压力大、某些药物的副作用或消化系统疾病。在中医理论中，消化不良可能与脾胃虚弱、湿阻中焦、食积内停等有关，治疗通常侧重于健脾和胃、调理气机、消食导滞。",en:"Indigestion refers to problems in the digestive process that lead to stomach discomfort, bloating, abdominal pain, nausea, or acid reflux. It can be caused by various factors, including improper diet, excessive alcohol consumption, stress, side effects of certain medications, or digestive system diseases. In traditional Chinese medicine (TCM), indigestion may be related to weakness of the spleen and stomach, damp obstruction in the middle burner, or food stagnation, with treatments focusing on strengthening the spleen and stomach, regulating qi, and eliminating food stagnation."},ze=[{name:{zh:"茯苓",en:"Poria"},effects:{zh:"能健脾利湿，对于因脾虚湿盛引起的消化不良（如腹胀、恶心）有很好的治疗效果",en:"Strengthens the spleen and drains dampness, effectively treating indigestion caused by spleen deficiency and excess dampness (such as bloating and nausea)"}},{name:{zh:"甘草",en:"Licorice"},effects:{zh:"具有补脾益气、清热解毒的作用，能够缓解胃部不适和胃炎症状",en:"Supplements the spleen and benefits qi, clears heat and detoxifies, relieving stomach discomfort and symptoms of gastritis"}},{name:{zh:"白术",en:"Atractylodes Macrocephala"},effects:{zh:"能健脾燥湿、补气固表，对于脾虚引起的消化不良（如食欲不振、腹胀）特别有效",en:"Strengthens the spleen, dries dampness, supplements qi, and stabilizes the exterior, particularly effective for indigestion caused by spleen deficiency (such as poor appetite and bloating)"}},{name:{zh:"山楂",en:"Hawthorn"},effects:{zh:"能够促进消化、去食积，对于因饮食过量或食物积滞引起的消化不良有显著效果",en:"Promotes digestion and removes food stagnation, significantly effective for indigestion caused by overeating or food stagnation"}}],ve=[{combination:{zh:"茯苓、白术和甘草",en:"Poria, Atractylodes Macrocephala, and Licorice"},effects:{zh:"可以共同作用于健脾利湿，增强消化功能，适合大部分消化不良的患者",en:"Work together to strengthen the spleen and drain dampness, enhancing digestive function, suitable for most patients with indigestion"}},{combination:{zh:"山楂与白术",en:"Hawthorn and Atractylodes Macrocephala"},effects:{zh:"不仅可以促进消化，还能健脾补气，适用于因食积或脾虚导致的消化不良",en:"Not only promotes digestion but also strengthens the spleen and supplements qi, suitable for indigestion caused by food stagnation or spleen deficiency"}}],_e={disease:ge,key:be,description:ye,recommended_herbs:ze,herbal_combinations:ve},Ie={zh:"肺炎",en:"Pneumonia"},we="pneumonia",Ce={zh:"肺炎是指肺部的炎症，通常由细菌、病毒或其他微生物引起。肺炎的典型症状包括发热、咳嗽、呼吸困难和胸痛。在中医中，肺炎可能被视为‘肺热’或‘痰湿阻肺’，治疗通常侧重于清热解毒、化痰止咳、宣肺平喘。",en:"Pneumonia is an inflammation of the lungs, usually caused by bacteria, viruses, or other microorganisms. Typical symptoms include fever, cough, difficulty breathing, and chest pain. In traditional Chinese medicine (TCM), pneumonia may be considered as 'lung heat' or 'phlegm-damp obstructing the lungs,' with treatments focusing on clearing heat and detoxifying, resolving phlegm and stopping cough, and ventilating the lungs to relieve asthma."},xe=[{name:{zh:"丹参",en:"Salvia Miltiorrhiza"},effects:{zh:"具有活血化瘀、清热解毒的功效，可以用于治疗因瘀血阻塞引起的肺部疾病，如肺炎，改善肺部血液循环，减轻炎症。",en:"Has the effects of promoting blood circulation to remove blood stasis and clearing heat and detoxifying. It can be used to treat lung diseases caused by blood stasis obstruction, such as pneumonia, improving lung blood circulation and reducing inflammation."}},{name:{zh:"菊花",en:"Chrysanthemum"},effects:{zh:"性寒，有清热解毒、散风热的作用，适用于治疗因外感风热引起的肺炎，能够缓解发热、咳嗽等症状。",en:"Is cold in nature and has the effects of clearing heat and detoxifying, dispersing wind-heat, suitable for treating pneumonia caused by exogenous wind-heat, relieving symptoms like fever and cough."}},{name:{zh:"白术",en:"Atractylodes Macrocephala"},effects:{zh:"能健脾燥湿、补气固表，对于肺炎患者常见的脾虚湿盛症状，如乏力、食欲不振，有良好的治疗效果。",en:"Strengthens the spleen, dries dampness, supplements qi, and secures the exterior. It effectively treats common symptoms of pneumonia in patients with spleen deficiency and excessive dampness, such as fatigue and poor appetite."}},{name:{zh:"香附",en:"Cyperus Rotundus"},effects:{zh:"具有疏肝解郁、行气活血的作用，对于因情绪抑郁、气滞血瘀引起的肺炎症状有一定的缓解作用。",en:"Has the functions of soothing the liver, relieving depression, promoting qi circulation, and activating blood. It can alleviate pneumonia symptoms caused by emotional depression, qi stagnation, and blood stasis."}}],$e=[{combination:{zh:"丹参和菊花",en:"Salvia Miltiorrhiza and Chrysanthemum"},effects:{zh:"可以清热解毒、活血化瘀，适用于因瘀血或外感风热引起的肺炎。",en:"Can clear heat and detoxify, promote blood circulation to remove blood stasis, suitable for pneumonia caused by blood stasis or exogenous wind-heat."}},{combination:{zh:"白术与香附",en:"Atractylodes Macrocephala and Cyperus Rotundus"},effects:{zh:"可以健脾燥湿、疏肝解郁，适合因脾虚湿盛或情绪抑郁导致的肺炎患者。",en:"Can strengthen the spleen, dry dampness, soothe the liver, and relieve depression, suitable for pneumonia patients with spleen deficiency, excessive dampness, or emotional depression."}}],qe={disease:Ie,key:we,description:Ce,recommended_herbs:xe,herbal_combinations:$e},ke={zh:"尿路感染",en:"Urinary Tract Infection (UTI)"},Se="urinary",Ae={zh:"尿路感染（Urinary Tract Infection, UTI）是指尿路系统（包括膀胱、尿道、肾脏等）的感染。它可以分为上尿路感染和下尿路感染，其中最常见的是下尿路感染，表现为尿急、尿频、尿痛等症状。尿路感染主要由细菌引起，尤其是大肠杆菌。在中医中，尿路感染可能与湿热下注、气虚不固等有关，治疗通常侧重于清热利湿、补气固表。",en:"Urinary Tract Infection (UTI) refers to an infection in the urinary system, including the bladder, urethra, and kidneys. It can be categorized into upper and lower urinary tract infections, with the latter being more common, presenting symptoms such as urgency, frequency, and pain during urination. UTIs are primarily caused by bacteria, especially E. coli. In traditional Chinese medicine (TCM), UTI may be related to damp-heat pouring downward or qi deficiency not securing, with treatments focusing on clearing heat and draining dampness, supplementing qi, and securing the exterior."},Te=[{name:{zh:"茯苓",en:"Poria"},effects:{zh:"能健脾利湿，对于因湿热引起的尿路感染有很好的治疗效果。它可以帮助清除体内的湿热，减轻尿频、尿急等症状。",en:"Strengthens the spleen and drains dampness, effectively treating UTIs caused by damp-heat. It helps clear damp-heat from the body and alleviate symptoms like frequent urination and urgency."}},{name:{zh:"菊花",en:"Chrysanthemum"},effects:{zh:"性寒，有清热解毒、平肝明目的作用，适用于治疗因肝火过旺引起的尿路感染，能够缓解发热、尿痛等症状。",en:"Is cold in nature and has the effects of clearing heat and detoxifying, calming the liver, and improving vision. It is suitable for treating UTIs caused by excessive liver fire, relieving symptoms like fever and painful urination."}},{name:{zh:"桔梗",en:"Platycodon Grandiflorum"},effects:{zh:"能够宣肺解表、利咽通络，对于上呼吸道感染引起的尿路感染有辅助治疗作用，帮助改善呼吸系统的健康状况。",en:"Can disperse the lung and resolve phlegm, benefit the throat, and promote the circulation of qi. It offers auxiliary treatment for UTIs caused by upper respiratory tract infections, helping to improve the health of the respiratory system."}},{name:{zh:"泽泻",en:"Alisma Plantago-aquatica"},effects:{zh:"具有利水渗湿、清热泻火的功效，适用于治疗因内热过盛引起的尿路感染，可以帮助减轻尿频、尿急等症状。",en:"Has diuretic, dampness-draining, heat-clearing, and fire-purging effects, suitable for treating UTIs caused by excessive internal heat, helping to alleviate symptoms like frequent urination and urgency."}}],Me=[{combination:{zh:"茯苓和泽泻",en:"Poria and Alisma Plantago-aquatica"},effects:{zh:"可以加强利水渗湿的作用，特别适合湿热型尿路感染患者。",en:"Strengthens the diuresis and dampness-draining effect, especially suitable for patients with damp-heat type UTIs."}},{combination:{zh:"菊花与桔梗",en:"Chrysanthemum and Platycodon Grandiflorum"},effects:{zh:"可以清热解毒、宣肺解表，适合因肝火过旺或上呼吸道感染引起的尿路感染。",en:"Can clear heat and detoxify, disperse the lung, and resolve phlegm, suitable for UTIs caused by excessive liver fire or upper respiratory tract infections."}}],Pe={disease:ke,key:Se,description:Ae,recommended_herbs:Te,herbal_combinations:Me},He={components:{DiseaseDetail:L},data(){return{activeHerbName:"angina",diseases:[F,X,ae,de,pe,_e,qe,Pe]}},mounted(){this.activeHerbName=window.location.hash.substring(1)||"angina"}};function Ue(t,f,w,a,r,Ge){const C=l("el-header"),x=l("disease-detail"),$=l("el-tab-pane"),q=l("el-tabs"),k=l("el-main"),S=l("el-container");return o(),u(S,null,{default:e(()=>[s(C,null,{default:e(()=>[i("疾病介绍")]),_:1}),s(k,null,{default:e(()=>[s(q,{modelValue:r.activeHerbName,"onUpdate:modelValue":f[0]||(f[0]=c=>r.activeHerbName=c),"tab-position":"left",style:{height:"100vh"}},{default:e(()=>[(o(!0),m(p,null,g(r.diseases,c=>(o(),u($,{key:c.key,label:`${c.disease.zh} / ${c.disease.en}`,name:c.key},{default:e(()=>[s(x,{diseaseData:c},null,8,["diseaseData"])]),_:2},1032,["label","name"]))),128))]),_:1},8,["modelValue"])]),_:1})]),_:1})}const De=v(He,[["render",Ue],["__scopeId","data-v-fc3ea8d5"]]),I=M(De);I.use(H);I.mount("#app-disease");
